import React, { useState, useEffect } from 'react';
import ProductItem from './components/ProductItem';
import { PRODUCTS, FREE_GIFT, THRESHOLD } from './data';

function App() {
  const [cart, setCart] = useState([]);
  const [freeGiftAdded, setFreeGiftAdded] = useState(false);

  const calculateSubtotal = () => {
    return cart.reduce((total, item) => total + item.product.price * item.quantity, 0);
  };

  const subtotal = calculateSubtotal();
  const remainingAmount = Math.max(0, THRESHOLD - subtotal);
  const progress = Math.min(1, subtotal / THRESHOLD) * 100;

  const handleAddToCart = (productToAdd) => {
    const existingItem = cart.find(item => item.product.id === productToAdd.id);

    if (existingItem) {
      setCart(
        cart.map(item =>
          item.product.id === productToAdd.id ? { ...item, quantity: item.quantity + productToAdd.quantity } : item
        )
      );
    } else {
      setCart([...cart, { product: productToAdd, quantity: productToAdd.quantity }]);
    }
  };

  const handleUpdateQuantity = (productId, newQuantity) => {
    if (newQuantity > 0) {
      setCart(
        cart.map(item =>
          item.product.id === productId ? { ...item, quantity: newQuantity } : item
        )
      );
    } else {
      handleRemoveFromCart(productId);
    }
  };

  const handleRemoveFromCart = (productId) => {
    setCart(cart.filter(item => item.product.id !== productId));
  };

  useEffect(() => {
    if (subtotal >= THRESHOLD && !freeGiftAdded) {
      setCart(prevCart => [...prevCart, { product: FREE_GIFT, quantity: 1 }]);
      setFreeGiftAdded(true);
      alert(`Congratulations! A free ${FREE_GIFT.name} has been added to your cart.`);
    } else if (subtotal < THRESHOLD && freeGiftAdded) {
      setCart(prevCart => prevCart.filter(item => item.product.id !== FREE_GIFT.id));
      setFreeGiftAdded(false);
    }
  }, [subtotal, freeGiftAdded]);

  const buttonStyleRemove = {
    marginLeft: '10px',
    background: 'rgb(230 143 137)',
    color: 'white',
    borderRadius: '7px',
    outline: '0',
    padding: '0.5em 2em',
    border: '0',
    cursor: 'pointer',
  };
  const container = {
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)',
    gap: '15px',
  };
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
      <h1>Shopping Cart</h1>
<div style={container}>
  {PRODUCTS.map(product => (
    <ProductItem key={product.id} product={product} onAddToCart={handleAddToCart} />
  ))}
</div>

      <h2>Shopping Cart</h2>
      <div style={{ backgroundColor: '#f0f0f0', border: '1px solid #ccc', height: '20px', width: '200px', borderRadius: '5px', margin: '10px 0' }}>
        <div
          style={{
            backgroundColor: 'lightblue',
            height: '100%',
            width: `${progress}%`,
            borderRadius: '5px',
          }}
        ></div>
      </div>
      <p>Spend ${THRESHOLD} to get a free {FREE_GIFT.name}! You are ${remainingAmount.toFixed(2)} away.</p>

      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cart.map(item => (
            <li key={item.product.id} style={{ borderBottom: '1px solid #eee', paddingBottom: '10px', marginBottom: '10px', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
              <div>
                <h3>{item.product.name}</h3>
                <p>Price: ${item.product.price}</p>
              </div>
              {item.product.id !== FREE_GIFT.id && (
                <div>
                  <button onClick={() => handleUpdateQuantity(item.product.id, item.quantity - 1)}>-</button>
                  <span style={{ margin: '0 10px' }}>{item.quantity}</span>
                  <button onClick={() => handleUpdateQuantity(item.product.id, item.quantity + 1)}>+</button>
                  <button style={buttonStyleRemove} onClick={() => handleRemoveFromCart(item.product.id)} >Remove</button>
                </div>
              )}
              {item.product.id === FREE_GIFT.id && (
                <span style={{ fontStyle: 'italic', color: 'green' }}>Free Gift</span>
              )}
            </li>
          ))}
        </ul>
      )}
      <p><strong>Subtotal: ${subtotal.toFixed(2)}</strong></p>
    </div>
  );
}

export default App;
