import React, { useState } from 'react';

function ProductItem({ product, onAddToCart }) {
  const [quantity, setQuantity] = useState(1);

  const handleIncrement = () => {
    setQuantity(quantity + 1);
  };

  const handleDecrement = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const handleAddToCartClick = () => {
    onAddToCart({ ...product, quantity }); // Pass quantity with the product
    setQuantity(1); // Reset quantity after adding
  };

  const buttonStyleAdd = {
    background: '#3b3bff',
    color: 'white',
    borderRadius: '7px',
    outline: '0',
    padding: '0.5em 2em',
    border: '0',
    cursor: 'pointer',
  };
  return (
    <div style={{ border: '1px solid #ccc', padding: '10px', display: 'flex', flexDirection: 'column' }}>
      <div>
        <h3>{product.name}</h3>
        <p>Price: ${product.price}</p>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}> {}
        <button onClick={handleDecrement}>-</button>
        <span style={{ margin: '0 10px' }}>{quantity}</span>
        <button onClick={handleIncrement}>+</button>
      </div>
     <div>
     <button style={buttonStyleAdd} onClick={handleAddToCartClick}>Add to Cart</button>
     </div>
    </div>
  );
}

export default ProductItem;
